	(1637, 'trigger:hunter viper sting on attacker', 1, 'viper sting on attacker [h:object|trigger] [c:viper sting on attacker|trigger now]\r\n\r\nCombat behavior:\r\nExecutes: [h:action:hunter viper sting on attacker|viper sting on attacker] (21.000) for [h:strategy:hunter sting beast mastery pve|sting beast mastery pve]\r\nExecutes: [h:action:hunter viper sting on attacker|viper sting on attacker] (21.000) for [h:strategy:hunter sting beast mastery pvp|sting beast mastery pvp]\r\nExecutes: [h:action:hunter viper sting on attacker|viper sting on attacker] (21.000) for [h:strategy:hunter sting beast mastery raid|sting beast mastery raid]\r\nExecutes: [h:action:hunter viper sting on attacker|viper sting on attacker] (21.000) for [h:strategy:hunter sting marksmanship pve|sting marksmanship pve]\r\nExecutes: [h:action:hunter viper sting on attacker|viper sting on attacker] (21.000) for [h:strategy:hunter sting marksmanship pvp|sting marksmanship pvp]\r\nExecutes: [h:action:hunter viper sting on attacker|viper sting on attacker] (21.000) for [h:strategy:hunter sting marksmanship raid|sting marksmanship raid]\r\nExecutes: [h:action:hunter viper sting on attacker|viper sting on attacker] (21.000) for [h:strategy:hunter sting survival pve|sting survival pve]\r\nExecutes: [h:action:hunter viper sting on attacker|viper sting on attacker] (21.000) for [h:strategy:hunter sting survival pvp|sting survival pvp]\r\nExecutes: [h:action:hunter viper sting on attacker|viper sting on attacker] (21.000) for [h:strategy:hunter sting survival raid|sting survival raid]\r\nExecutes: [h:action:hunter viper sting on attacker|viper sting on attacker] (21.000) for [h:strategy:hunter sting viper|sting viper]', '', '', '', '', '', '', '', '', '', 0),
	(1638, 'trigger:reset values', 1, 'reset values [h:object|trigger] [c:reset values|trigger now]\r\n\r\nCombat behavior:\r\nExecutes: [h:action|reset values] (100.000) for [h:strategy|chat]\r\nNon combat behavior:\r\nExecutes: [h:action|reset values] (100.000) for [h:strategy|chat]\r\nDead state behavior:\r\nExecutes: [h:action|reset values] (100.000) for [h:strategy|chat]\r\nReaction behavior:\r\nExecutes: [h:action|reset values] (100.000) for [h:strategy|chat]', '', '', '', '', '', '', '', '', '', 0),
	(1639, 'action:pull action', 1, 'pull action [h:object|action] [c:do pull action|execute]\r\n\r\nCombat behavior:\r\nTriggers from: [h:trigger:pull start|pull start] with relevance (30.000) for [h:strategy:druid pull|pull]\r\nNon combat behavior:\r\nTriggers from: [h:trigger:pull start|pull start] with relevance (30.000) for [h:strategy:druid pull|pull]', '', '', '', '', '', '', '', '', '', 0),
